mapper.update:
update category
name = #{name}, type = #{type}, sort = #{sort}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select id, type, name, sort, status, create_time, update_time, create_user, update_user from category

    and name = #{name}
    and type = #{type}
